mapper.select:
 SELECT * FROM (
 SELECT
 ROWNUM AS RNUM,
 T.*
 FROM (
		SELECT * FROM TB_SM01M01
 ) T
 ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

 SELECT * FROM (
 SELECT
 ROWNUM AS RNUM,
 T.*
 FROM (
		SELECT * FROM TB_SM01M01
 ) T
 ) WHERE RNUM BETWEEN #{firstIndex} AND #{lastIndex}

SELECT COUNT(*)
FROM TB_CR01D01 A
WHERE A.CO_CD = #{coCd} AND A.EST_NO = #{estNo} AND A.EST_DEG = #{estDeg}

SELECT *
FROM TB_CR01D01
WHERE
    1 = 1
    AND CO_CD = #{coCd}
    AND EST_NO = #{estNo}
    AND EST_DEG = #{estDeg}

mapper.delete:
DELETE
FROM TB_CR01M01
WHERE 1 = 1
    AND CO_CD = #{coCd}
    AND EST_NO = #{estNo}
    AND EST_DEG = #{estDeg}

DELETE
FROM TB_CR01D01
WHERE 1 = 1
    AND CO_CD = #{coCd}
    AND EST_NO = #{estNo}
    AND EST_DEG = #{estDeg}
    AND EST_SEQ = #{estSeq}

DELETE
FROM TB_CR01D01
WHERE 1 = 1
  AND CO_CD = #{coCd}
  AND EST_NO = #{estNo}
  AND EST_DEG = #{estDeg}

mapper.update:
UPDATE TB_CR01M01
SET ORDRS_YN = 'Y',
    ORDRS_DT = #{ordrsDt}

WHERE CO_CD = #{coCd}
  AND EST_NO = #{estNo}

mapper.select:
SELECT MAX(EST_DEG)
FROM TB_CR01M01

WHERE CO_CD = #{coCd} AND EST_NO = #{estNo}
